package Javabasics;

public class Fizzbuzz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int n=1;
	
while(n<31)		
{
	
	if (n%15==0)
		
		
		
		System.out.println("fizzBuzz");
	else if(n%3==0)

    	  
			System.out.println("fizz");
			
      
      else if (n%5==0)
		      
			System.out.println("Buzz");
		      
       
				
			
      else 
      
			System.out.println(n);
			
		
		n++;
	}
	}
	
	}



